import { create } from 'zustand';
import axios from 'axios';

const API_BASE = 'http://localhost:8000'; // Make sure this points to your FastAPI backend

const useStore = create((set, get) => ({
  // Auth state
  user: null,
  isAuthenticated: false,

  // Data state
  uploadedFile: null,
  uploadedFiles: [], // Add this array
  previewData: null,
  generationTasks: [],
  currentTask: null,

  // UI state
  loading: false,
  error: null,

  // Actions
  setLoading: (loading) => set({ loading }),
  setError: (error) => set({ error }),

  // File upload
  uploadFile: async (file) => {
    set({ loading: true, error: null });

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post(`${API_BASE}/upload`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
        timeout: 10000
      });

      set(state => ({ 
        uploadedFile: response.data,
        uploadedFiles: [...(state.uploadedFiles || []), response.data],
        loading: false 
      }));

      return response.data;
    } catch (error) {
      set({ 
        error: 'Upload failed: ' + error.message,
        loading: false 
      });
      throw error;
    }
  },

  // Generate preview
  generatePreview: async (fileId, rows = 10) => {
    set({ loading: true, error: null });
    try {
      const response = await axios.post(`${API_BASE}/preview`, {
        file_id: fileId,
        n_rows: rows
      });

      set({ 
        previewData: response.data,
        loading: false 
      });

      return response.data;
    } catch (error) {
      set({ 
        error: error.response?.data?.detail || 'Preview failed',
        loading: false 
      });
      throw error;
    }
  },

  // Start generation task
  startGeneration: async (params) => {
    set({ loading: true, error: null });
    try {
      const response = await axios.post(`${API_BASE}/generate-async`, params);

      const newTask = response.data;
      set(state => ({ 
        generationTasks: [...state.generationTasks, newTask],
        currentTask: newTask,
        loading: false 
      }));

      return newTask;
    } catch (error) {
      set({ 
        error: error.response?.data?.detail || 'Generation failed',
        loading: false 
      });
      throw error;
    }
  },

  // Poll task status
  pollTaskStatus: async (taskId) => {
    try {
      const response = await axios.get(`${API_BASE}/tasks/${taskId}/status`);
      const updatedTask = response.data;

      set(state => ({
        generationTasks: state.generationTasks.map(task => 
          task.id === taskId ? updatedTask : task
        ),
        currentTask: state.currentTask?.id === taskId ? updatedTask : state.currentTask
      }));

      return updatedTask;
    } catch (error) {
      console.error('Failed to poll task status:', error);
    }
  },

  // Get all tasks
  fetchTasks: async () => {
    try {
      const response = await axios.get(`${API_BASE}/tasks`);
      set({ generationTasks: response.data });
      return response.data;
    } catch (error) {
      set({ error: 'Failed to fetch tasks' });
    }
  },

  // Check task status
  checkTaskStatus: async (taskId) => {
    try {
      const response = await axios.get(`${API_BASE}/task-status/${taskId}`);
      const updatedTask = response.data;

      set(state => ({
        currentTask: updatedTask,
        generationTasks: state.generationTasks.map(task => 
          task.task_id === taskId ? updatedTask : task
        )
      }));

      return updatedTask;
    } catch (error) {
      console.error('Failed to check task status:', error);
    }
  }
}));

export default useStore;
